import './styles/login-table.sass';
import { faUser, faLock } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { useDispatch, useSelector } from 'react-redux';
import { setPassword, setUsername } from './redux/login';
import { useRef } from 'react';

function LoginTable() {

    const { count } = useSelector((state) => state.login);
    const dispatch = useDispatch();
    const usernameRef = useRef();
    const passwordRef = useRef();

    const handleSubmit = (e) => {
        e.preventDefault();
        dispatch(setUsername(usernameRef))
        dispatch(setPassword(passwordRef))
    }

    return (
        <div>
            <div className="container">
                <form onSubmit={handleSubmit}>
                    <div className="input-container">
                        <input className="input" type="email" name="username" placeholder='Username' required refer={usernameRef}></input>
                        <FontAwesomeIcon className="icon" icon={faUser} />
                    </div>
                    <div className="input-container">
                        <input className="input" type="password" name="password" placeholder='Password' required refer={passwordRef}></input>
                        <FontAwesomeIcon className="icon" icon={faLock} />
                    </div>
                    <div className="button-container">
                        <button className="button">Sign In</button>
                    </div>
                </form>
            </div>
            <div className="shadow">
                <div className="shadow mirror"></div>
            </div>
        </div>

    );
  }

  export default LoginTable;
