import './styles/login-table.sass';
import Popup from './popup/popup';
import { faUser, faLock } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { useDispatch, useSelector } from 'react-redux';
import { setPassword, setUsername } from './redux/login';
import { useEffect, useState } from "react";

function LoginTable() {

    const { username, password } = useSelector((state) => state.login);
    const dispatch = useDispatch();
    const [popup, setPopup] = useState(true);
    const [user, setUser] = useState(() => {
        const saved = localStorage.getItem("username");
        const initialValue = JSON.parse(saved);
        return initialValue || "";
    });
    const [pass, setPass] = useState(() => {
        const saved = localStorage.getItem("password");
        const initialValue = JSON.parse(saved);
        return initialValue || "";
    });

    async function loginRequest () {
        const res = await fetch("/api/users", {method: "POST", body: JSON.stringify({user, pass})});
        const status = await res.json();
        return status.status;
    }

    useEffect(() => {
        const checkLocalMemory = () => {
            if(loginRequest()){
                //alert("You are logged in! \n Welcome back!");
            }
        }
        checkLocalMemory();
    },[])

    const handleSubmit = async (e) => {
        e.preventDefault();
        dispatch(setUsername(user))
        dispatch(setPassword(pass))
        if(loginRequest()){
            alert("You are logged in!");
            localStorage.setItem("username", JSON.stringify(user));
            localStorage.setItem("password", JSON.stringify(pass));
        }
    }

    return (
        <div>
            <form className="container" onSubmit={handleSubmit} >
                <div className="input-container">
                    <input className="input" type="email" name="username" value={user} placeholder='Username' required onChange={e => setUser(e.target.value)}></input>
                    <FontAwesomeIcon className="icon" icon={faUser} />
                </div>
                <div className="input-container">
                    <input className="input" type="password" name="password" value={pass} placeholder='Password' required onChange={e => setPass(e.target.value)}></input>
                    <FontAwesomeIcon className="icon" icon={faLock} />
                </div>
                <div className="button-container">
                    <button className="button">Sign In</button>
                </div>
            </form>
            <div className="shadow">
                <div className="shadow mirror"></div>
            </div>
            <Popup trigger={popup} setTrigger={setPopup}>
                <h3>POPUP</h3>
            </Popup>
        </div>

    );
  }

  export default LoginTable;
