import { createServer } from "miragejs";

let users = [
    {id: 1, email: "labas@a", pass: "abc"},
]

createServer({
    routes() {
        this.namespace = "api"

        this.post("/users", (schema, request) => {
            let attrs = JSON.parse(request.requestBody);
            let user = attrs.user;
            let status = false;
            if(users.find()){
                status = true;
            }
            console.log(users);
            return {
                status: status
            }
        })
    },
})
